int radius;
ArrayList<Balloon> balloons;

void setup()
{
    size(400,400);
    balloons = new ArrayList<Balloon>();
    balloons.add(new Balloon());
    noStroke();
    frameRate(30);
}


void draw(){
    background(150,255,255);


    for(int i = 0;i < balloons.size(); i++)  {
        b = balloons.get(i);
        if(b.y-b.radius < 40)  {
            balloons.remove(i);
            i--;
        }
        b.draw();  
        b.update();
    }
    if(frameCount%10==0)  {
        balloons.add(new Balloon());
    }

    fill(220,220,220);
    int offset = frameCount%width;
    for(int i = 0; i < width; i=i+20)  {        
        triangle(i, 0, i+10, 40, i+20, 0);
    }
}

class Balloon  {
    int x;
    int y;
    int radius;
    int offset;
    int shake;
    int r;
    int g;
    int b;

    Balloon()  {
        radius = random(30)+10;
        x = random(width);
        y = height+radius;
        offset = random(5);
        shake = random(80)+2;
        r = random(155)+100;
        g = random(155)+100;
        b = random(155)+100;
    }

    void update()  {
        y = y-1;
        x = x+sin((frameCount+offset)/shake)*5;
    }

    void draw()  {
        fill(r,g,b);
        ellipse(x,y,7*(radius*2)/8,radius*2);
        triangle(x,y+radius, x-(radius/6), y+radius+(radius/6), x+(radius/6), y+radius+(radius/6));
    }
}
